package mydev.uix; import java.awt.Button; import java.awt.Color; import java.awt.FlowLayout; import java.awt.Panel; public class YellowAutoButton extends Panel { protected Button btAuto; public YellowAutoButton(Button autoBtn) { super(); setLayout(new FlowLayout(FlowLayout.LEFT,getBorderSpace()+1,getBorderSpace())); setBackground(getBorderColor()); if(autoBtn !=null) addAuto(autoBtn);} public YellowAutoButton() { this(new Button("Auto"));} public void enableCtrl(boolean enable) { btAuto.enable(enable);} protected void addAuto(Button autoBtn) { add(btAuto=autoBtn); btAuto.enable(true);} public Button getBtAuto() { return btAuto;} public Color getBorderColor() { return Color.yellow;} public int getBorderSpace() { return 1+1;}}