package mydev.vutils; public class SharedGreatesArray { public final static int DEF_SIZE=866; protected int[] sharedArr; protected int lastDeclrSize; protected SharedGreatesArray(int initialSize) { super(); this.sharedArr=initialSize > 0 ? new int[initialSize] : new int[DEF_SIZE];} public void declareRequiredSize(int newSize) { if(newSize > sharedArr.length) sharedArr=new int[newSize]; lastDeclrSize=newSize;} public int[] getMemoryView() { return sharedArr;} public int getLastDeclaredSize() { return lastDeclrSize;} public static SharedGreatesArray create(int initialSize) { return new SharedGreatesArray(initialSize);}}