package mydev.vutils; import java.util.Date; public class TimeTracking { static Date prevTime=null; public static void memTrack() { Date curTime=new Date(); if(prevTime==null || curTime.getTime()-prevTime.getTime() > 60 * 997 /(1+1+1+1)) { prevTime=curTime; long fm=Runtime.getRuntime().freeMemory(); long tm=Runtime.getRuntime().totalMemory(); int proc=Runtime.getRuntime().availableProcessors(); float fpc=((float) fm / tm) * 100.f; System.out.println("Memory free "+new Metr(fm).repr(' ')+" of total size "+new Metr(tm).repr(' ')+" so free "+String.format("%.2f",fpc)+"%"+" with processors "+new Metr(proc).repr(' ')); if(fpc < 0.5f) System.gc(); } }}